INSERT INTO product (id, name, description, price_euro)
    VALUES (10001, 'Alberto Caffè Crema 1kg ganze Bohnen', 'Alberto Caffé Crema ist ein aromatisch würziger Kaffee mit samtig-feiner Crema. Die spezielle Röstkaffeemischung ausgesuchter Provenienzen bietet ein sinnliches Genusserlebnis.', 9.99);

INSERT INTO product (id, name, description, price_euro)
VALUES (10002, 'Zicaffe Il Tuo Bohnen 1kg', 'Espresso mit sizilianischem Feuer. Mild im Geschmack mit feinen süditalienischen Mokkatönen. Lang anhaltende kräftige Crema, ideal für den perfekten Espresso. Kleinste Mengen mit und ohne Zucker und volles süditalienisches Espresso-Flair.', 11.49);

INSERT INTO product (id, name, description, price_euro)
VALUES (10003, 'Lavazza Cafe Espresso Lavazza gemahlen 250g', 'Der beste Duft für alle, die den reinen Genuss des Espresso lieben. Gemahlen erhältlich. Röstung: Mittel', 4.99);

INSERT INTO product (id, name, description, price_euro)
VALUES (10004, 'Rancilio Silvia E Siebträger, Espressomaschine', 'Rancilio Silvia E - Verbrauch weniger als 0,5 Watt im Standby-Modus und automatische Abschaltung nach 30 Minuten Inaktivität. Zu Hause wie in der Bar. Jederzeit einen perfekten Espresso genießen, ganz einfach, wie in der Bar. Ausgesuchte, robuste Materialien, (Edelstahlgehäuse) und schlichtes Design, das sich in jeden Einrichtungsstil einfügt.', 489.00);

INSERT INTO product (id, name, description, price_euro)
VALUES (10005, 'Quick Mill Stretta Modell 0820 Kaffeehalbautomat - Schwarz', 'Manuelle Kaffeemaschine mit exklusivem Filterkopf "Quick Mill" aus Edelstahl mit einrastendem Schraubsystem und O-Ring-Dichtung. Exklusiver Thermoblock aus Metall mit doppelter thermostatischer Kontrolle und Schmelzsicherung. Unbeschränkte Dampf- und Heisswasserentnahme. Dampfauslass komplett mit Lufteinzug um Getränke aufzuwärmen oder für die Zubereitung von "Cappuccino".', 389.00);

INSERT INTO product (id, name, description, price_euro)
VALUES (10006, 'Lavazza Crema Classico 1000g', 'Mit Caffè Crema Classico bietet Lavazza eine Mischung ganzer Bohnen mit harmonischem Geschmack und aromatischen Noten. Die Mischung aus 30% Robusta- und 70% Arabica-Bohnen hat eine mittlere Röstung.', 12.49);

INSERT INTO product (id, name, description, price_euro)
VALUES (10007, 'Mahlkönig VARIO home Kaffeemühle', 'Im Bereich Haushaltsmühlen ist sie als Nachfolgerin der 2014er Vario, für den preisbewussten Heimbarista konzipiert. Wenn Sie eine kompakt gebaute Kaffeemühle der preislichen Mittelklasse mit überdurchschnittlicher Leistung für den Hausgebrauch suchen, können wir ihnen die VARIO home absolut empfehlen. Sie bietet viel Ausstattung für relativ kleines Geld, benötigt wenig Platz und lässt sich leicht bedienen. Das Design polarisiert die Fangemeinde – den einen gefällt die unkonventionelle Optik dieser Kaffeemühle, während andere das moderne Aussehen mit großem LC-Display und Schiebern bestenfalls gewöhnungsbedürftig finden. Aber die Optik ist eine Sache, wichtiger ist das, was die Kaffeemühle leistet. Und in Sachen Leistung und Verarbeitungsqualität kann sich die „Kleine“ von Mahlkönig auf jeden Fall sehen lassen.', 362.00);